mapper.select:
SELECT ann_no AS annno, total_ann_no AS totalannno, total_title AS totaltitle, competent_ministry AS competentministry, spec_institution AS specinstitution, ann_tech_field_name AS checkedGisul, ann_type AS anntype, sub_ann_count AS subanncount, reception_type AS receptiontype, status AS status, TO_CHAR(started_at, 'YYYY-MM-DD') AS startedat, closed_at AS closedat, TO_CHAR(created_at, 'YYYY-MM-DD') AS createdat, updated_at AS updatedat, bucket_no AS bucketno FROM announcement WHERE 1 = 1
AND total_title LIKE '%' || #{keyword} || '%'
AND EXTRACT(YEAR FROM created_at) = #{date}
AND status = #{activeText}
AND competent_ministry IN
#{competent_ministry}
AND spec_institution IN
#{spec_institution}
AND ann_tech_field_name IN
#{ann_tech_field_name}
ORDER BY
created_at ASC
created_at DESC

SELECT ann_tech_field_name as annTechFieldName
from announcement
where ann_no = #{annNo}
