mapper.select:
SELECT COUNT(*)
FROM rnd_plan
where sub_ann_no = #{id}

mapper.update:
UPDATE evaluation_member
SET recv_status     = #{evalMemberRequest.recvStatus},
    recv_status_eng = #{evalMemberRequest.recvStatusEng}
WHERE mem_no = #{memberId}
  AND eval_committee_no = #{notiContentNo}

UPDATE noti
SET read_state = '확인완료'
WHERE notification_no = #{id}
