mapper.select:
SELECT task_name, rnd_task_no
FROM rnd_plan
WHERE rnd_plan_no = #{rndPlanNo}

SELECT name, weight, rank
FROM rnd_field
WHERE rnd_plan_no = #{rndPlanNo}

mapper.update:
UPDATE rnd_plan
SET task_name = #{taskName}
WHERE rnd_plan_no = #{rndPlanNo}

mapper.select:
SELECT curr_step
FROM rnd_plan
WHERE rnd_plan_no = #{rndPlanNo}

mapper.update:
UPDATE rnd_plan
SET curr_step = #{currStep}
WHERE rnd_plan_no = #{rndPlanNo}

UPDATE rnd_plan
SET final_tgt_content = #{finalTgtContent},
    perf_content = #{perfContent},
    rnd_content = #{rndContent}
WHERE rnd_plan_no = #{rndPlanNo}
